mapper.update:
update Question
set vcount = vcount+1
where q_no=#{q_no}

update Question
set title=#{title}, content=#{content}, updatedate=sysdate , youtubelink=#{youtubelink}
where q_no=#{q_no}

update qfile
set originalName=#{originalName}, changeName=#{changeName}
where q_no=#{q_no}

update Question
set status='N'
where q_no=#{q_no}

update Question
set fileox='Y'
where q_no=#{q_no}

update Question
set fileox='N'
where q_no=#{q_no}

mapper.select:
	select *
    from qfile
    where q_no=#{q_no} and originalName=#{originalName}

mapper.update:
update Question
set isreply='Y', replydate=sysdate, replycontent=#{replycontent}
where q_no=#{q_no}

update QReply
set status='N'
where r_no=#{r_no}
