mapper.select:
  
   select  m_no
	from myclass
	where lec_no=#{lec_no}

mapper.update:
update surveyquestion set 
answer=#{answer},
issurvey='Y'
where m_no=#{m_no}
And su_no=#{su_no}
And sq_no=#{sq_no}

mapper.select:
		select sq.sq_no, sq.question, sq.answer, sq.su_no
        from surveyquestion sq
		where sq.m_no=#{m_no} and sq.su_no=#{su_no}
