mapper.select:
select l.*
from myclass m, lecture l
where l.lec_no=m.lec_no and m.m_no=#{m_no}

select vd.* , v.vacation_title , v.reason , v.m_no ,m.name , l.title
from vacationdate vd , vacation v, member m , lecture l
where v.v_no =#{v_no} and m.m_no = v.m_no and vd.lec_no=l.lec_no and v.v_no = vd.v_no and vd.lec_no =#{lec_no}

mapper.update:
update vacationdate
set tstatus='Y'
where v_no=#{v_no} and lec_no=#{lec_no}

update vacationdate
set tstatus='B', companion_reason=#{companion_reason}
where v_no= #{v_no} and lec_no=#{lec_no}

update vacationdate
set astatus='Y', status='Y'
where v_no=#{v_no}

update vacationdate
set astatus='Y', status='Y'
where v_no=#{v_no} and lec_no=#{lec_no}
