mapper.select:
select l.*
from myclass m, lecture l
where l.lec_no=m.lec_no and m.m_no=#{m_no}

mapper.update:
update vacation
set tstatus='Y'
where v_no=#{v_no}

update vacation
set tstatus='B', companion_reason=#{companion_reason}
where v_no=#{v_no}

update vacation
set astatus='Y', status='Y'
where v_no=#{v_no}
